import React, { useEffect, useRef, useState } from 'react';

const CHAT_HISTORY_KEY = 'chatHistory';

const ChatDashboard = () => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const chatboxRef = useRef(null);

  useEffect(() => {
    const history = localStorage.getItem(CHAT_HISTORY_KEY);
    if (history) {
      setMessages(JSON.parse(history));
    }
  }, []);

  useEffect(() => {
    if (chatboxRef.current) {
      chatboxRef.current.scrollTop = chatboxRef.current.scrollHeight;
    }
  }, [messages]);

  const addMessage = (sender, message) => {
    const newMessages = [...messages, [sender, message]];
    setMessages(newMessages);
    localStorage.setItem(CHAT_HISTORY_KEY, JSON.stringify(newMessages));
  };

  const sendMessage = async () => {
    if (!input.trim()) return;
    addMessage('User', input);
    setInput('');
    setLoading(true);
    try {
      const response = await fetch(
        'https://backend.buildpicoapps.com/aero/run/llm-api?pk=v1-Z0FBQUFBQm9ZNDlUUXhRX1BxdzdNb0JBYWFNdjRHbGhPLXp6YkVJRlpoMG5DeWUweHphS3VtN1FUbWZ6YUFpODNOVk1NNmp0LXVxOFhjN2p5aHFrUEJ1Yl94S3cyRF9hRVE9PQ==',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ prompt: input })
        }
      );
      const data = await response.json();
      setLoading(false);
      if (data.status === 'success') {
        addMessage('CHANAKYA GPT', data.text);
      } else {
        addMessage('CHANAKYA GPT', 'Sorry, there was an error processing your request.');
      }
    } catch (error) {
      setLoading(false);
      addMessage('CHANAKYA GPT', 'Unable to connect to the server. Please try again later.');
    }
  };

  const handleInputKeyDown = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen font-serif">
      <div className="max-w-2xl mx-auto p-4">
        <header style={{ backgroundColor: '#d9f0ff' }} className="text-center p-4 rounded">
          <h1 className="text-2xl font-bold">CHANAKYA DOCTOR AI</h1>
          <p className="text-sm">Experience AI Conversations</p>
        </header>
        <div ref={chatboxRef} className="bg-white shadow-md rounded p-4 mt-6 h-96 overflow-y-scroll">
          {messages.map(([sender, message], idx) => (
            <div key={idx} className="mb-4">
              {sender === 'User' ? (
                <div className="text-right">
                  <span style={{ backgroundColor: '#a3d5ff' }} className="text-white p-2 rounded-lg inline-block">{message}</span>
                </div>
              ) : (
                <div className="text-left">
                  <span style={{ backgroundColor: '#83c9f4' }} className="text-white p-2 rounded-lg inline-block">{message}</span>
                </div>
              )}
            </div>
          ))}
        </div>
        <div className="flex items-center mt-4">
          <input
            type="text"
            placeholder="Type your message..."
            className="flex-grow p-2 border border-gray-300 rounded-l"
            value={input}
            onChange={e => setInput(e.target.value)}
            onKeyDown={handleInputKeyDown}
            disabled={loading}
          />
          <button
            onClick={sendMessage}
            style={{ backgroundColor: '#a3d5ff' }}
            className="text-white p-2 rounded-r"
            disabled={loading}
          >
            Send
          </button>
          {loading && (
            <div className="spinner ml-2" style={{ border: '4px solid rgba(0,0,0,0.1)', width: 24, height: 24, borderRadius: '50%', borderLeftColor: '#09f', animation: 'spin 1s ease infinite' }} />
          )}
        </div>
        <style>{`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    </div>
  );
};

export default ChatDashboard;
